						&lt;![CDATA[&lt;infoShareBackgroundTaskConfig version="1.1"&gt;
  &lt;services&gt;
    &lt;!-- Default role that is used by the background task service when it runs as a service --&gt;
    &lt;service role="Default"&gt;
      &lt;matrix&gt;
        &lt;group name="Translations" maxExecutions="2"&gt;
          &lt;handlers&gt;
            &lt;add ref="CREATETRANSLATIONFROMLIST" /&gt;
            &lt;add ref="CREATETRANSLATIONFROMREPORT" /&gt;
            &lt;add ref="CREATETRANSLATION" /&gt;
            &lt;add ref="RELEASETRANSLATIONS" /&gt;
            &lt;!-- Legacy background tasks --&gt;
            &lt;add ref="PUSHTRANSLATIONS" /&gt;
            &lt;add ref="CREATETRANSLATIONS" /&gt;
            &lt;add ref="CREATEPRIVATETRANSLATIONS" /&gt;
          &lt;/handlers&gt;
        &lt;/group&gt;
        &lt;group name="Export" maxExecutions="2"&gt;
          &lt;handlers&gt;
            &lt;add ref="EXPORTFORPUBLICATION" /&gt;
            &lt;add ref="INBOXEXPORT" /&gt;
            &lt;add ref="REPORTEXPORT" /&gt;
            &lt;add ref="SEARCHEXPORT" /&gt;
            &lt;add ref="PUBLICATIONEXPORT" /&gt;
            &lt;!-- Synchronous postprocessing...so they are not part of the matrix! --&gt;
            &lt;!--PUBLISHINGSERVICEPROCESS--&gt;
            &lt;!--ZIPFILES --&gt;
          &lt;/handlers&gt;
        &lt;/group&gt;
        &lt;group name="SynchronizeToLiveContent" maxExecutions="1"&gt;
          &lt;handlers&gt;
            &lt;add ref="SYNCHRONIZETOLIVECONTENT" /&gt;
          &lt;/handlers&gt;
        &lt;/group&gt;
        &lt;group name="Others" maxExecutions="2"&gt;
          &lt;handlers&gt;
            &lt;add ref="THUMBNAILSUBMIT" /&gt;
            &lt;add ref="ISHBATCHIMPORT" /&gt;
          &lt;/handlers&gt;
        &lt;/group&gt;
      &lt;/matrix&gt;
      &lt;!-- The service will check for tasks to recover (= revoke the lease) --&gt;
      &lt;leaseRecovery isEnabled="true" interval="00:05:00" /&gt;
      &lt;!-- If no next tasks are present, the poller will sleep the specified period in the interval --&gt;
      &lt;poller isEnabled="true" interval="00:00:10" /&gt;
      &lt;!-- The service will aggregate tasks with the same aggregationId, 
      only when the last item is submitted longer than the gracePeriod the tasks will be aggregated.
      Note: if there are no tasks to aggregate, the aggregation will sleep the specified period in the interval --&gt;
      &lt;aggregationRecovery isEnabled="true" gracePeriod="00:10:00" interval="00:10:00" maximumRetries="3" /&gt;
    &lt;/service&gt;
    &lt;!-- The role that is used by the background task service when it runs in console mode --&gt;
    &lt;service role="Console"&gt;
      &lt;matrix&gt;
        &lt;group name="All" maxExecutions="1"&gt;
          &lt;handlers&gt;
            &lt;add ref="CREATETRANSLATION" /&gt;
            &lt;add ref="CREATETRANSLATIONFROMLIST" /&gt;
            &lt;add ref="CREATETRANSLATIONFROMREPORT" /&gt;
            &lt;add ref="PUSHTRANSLATIONS" /&gt;
            &lt;add ref="CREATETRANSLATIONS" /&gt;
            &lt;add ref="CREATEPRIVATETRANSLATIONS" /&gt;
            &lt;add ref="RELEASETRANSLATIONS" /&gt;
            &lt;add ref="EXPORTFORPUBLICATION" /&gt;
            &lt;add ref="INBOXEXPORT" /&gt;
            &lt;add ref="REPORTEXPORT" /&gt;
            &lt;add ref="SEARCHEXPORT" /&gt;
            &lt;add ref="PUBLICATIONEXPORT" /&gt;
            &lt;add ref="SYNCHRONIZETOLIVECONTENT" /&gt;
            &lt;add ref="THUMBNAILSUBMIT" /&gt;
            &lt;add ref="ISHBATCHIMPORT" /&gt;
          &lt;/handlers&gt;
        &lt;/group&gt;
      &lt;/matrix&gt;
      &lt;!-- The service will check for tasks to recover (= revoke the lease) --&gt;
      &lt;leaseRecovery isEnabled="false" interval="00:05:00" /&gt;
      &lt;!-- If no next tasks are present, the poller will sleep the specified period in the interval --&gt;
      &lt;poller isEnabled="true" interval="00:00:10" /&gt;
      &lt;!-- The service will aggregate tasks with the same aggregationId, 
      only when the last item is submitted longer than the gracePeriod the tasks will be aggregated.
      Note: if there are no tasks to aggregate, the aggregation will sleep the specified period in the interval --&gt;
      &lt;aggregationRecovery isEnabled="false" gracePeriod="00:10:00" interval="00:00:10" maximumRetries="3" /&gt;
    &lt;/service&gt;
  &lt;/services&gt;
  &lt;handlers&gt;
    &lt;!-- Translations =========================================================== --&gt;
    &lt;handler eventType="CREATETRANSLATION"&gt;
      &lt;scheduler executeSynchronously="false" /&gt;
      &lt;authorization type="authenticationContext" /&gt;
      &lt;execution timeout="01:00:00" recoveryGracePeriod="00:10:00" isolationLevel="None" useSingleThreadApartment="false" /&gt;
      &lt;activator&gt;
        &lt;net name="CreateTranslation"&gt;
          &lt;parameters /&gt;
        &lt;/net&gt;
      &lt;/activator&gt;
      &lt;errorHandler maximumRetries="3"&gt;
        &lt;actions&gt;
          &lt;!-- Don't retry if the source language does not exist or is not released --&gt;
          &lt;add errorNumber="-102" action="Ignore" delay="00:00:00" /&gt;
          &lt;add errorNumber="-2007" action="Ignore" delay="00:00:00" /&gt;
          &lt;!-- For a timeout or a lock, wait longer before retrying --&gt;
          &lt;add errorNumber="-140" action="Retry" delay="00:10:00" /&gt;
          &lt;!-- Just retry --&gt;
          &lt;add errorNumber="*" action="Retry" delay="00:01:00" /&gt;
        &lt;/actions&gt;
      &lt;/errorHandler&gt;
    &lt;/handler&gt;
    &lt;handler eventType="CREATETRANSLATIONFROMLIST"&gt;
      &lt;scheduler executeSynchronously="false" /&gt;
      &lt;authorization type="authenticationContext" /&gt;
      &lt;execution timeout="01:00:00" recoveryGracePeriod="00:10:00" isolationLevel="None" useSingleThreadApartment="false" /&gt;
      &lt;activator&gt;
        &lt;net name="CreateTranslationFromList"&gt;
          &lt;parameters /&gt;
        &lt;/net&gt;
      &lt;/activator&gt;
      &lt;aggregation aggregator="TranslationManagementAggregator" /&gt;
      &lt;errorHandler maximumRetries="3"&gt;
        &lt;actions&gt;
          &lt;!-- For a timeout or a lock, wait longer before retrying --&gt;
          &lt;add errorNumber="-140" action="Retry" delay="00:10:00" /&gt;
          &lt;!-- Just retry --&gt;
          &lt;add errorNumber="*" action="Retry" delay="00:01:00" /&gt;
        &lt;/actions&gt;
      &lt;/errorHandler&gt;
    &lt;/handler&gt;
    &lt;handler eventType="CREATETRANSLATIONFROMREPORT"&gt;
      &lt;scheduler executeSynchronously="false" /&gt;
      &lt;authorization type="authenticationContext" /&gt;
      &lt;execution timeout="01:00:00" recoveryGracePeriod="00:10:00" isolationLevel="None" useSingleThreadApartment="false" /&gt;
      &lt;activator&gt;
        &lt;net name="CreateTranslationFromReport"&gt;
          &lt;parameters&gt;
            &lt;parameter name="DocumentBaselineCompleteMode"&gt;ISHLatestReleased&lt;/parameter&gt;
          &lt;/parameters&gt;
        &lt;/net&gt;
      &lt;/activator&gt;
      &lt;errorHandler maximumRetries="3"&gt;
        &lt;actions&gt;
          &lt;!-- For a timeout or a lock, wait longer before retrying --&gt;
          &lt;add errorNumber="-140" action="Retry" delay="00:10:00" /&gt;
          &lt;!-- Just retry --&gt;
          &lt;add errorNumber="*" action="Retry" delay="00:01:00" /&gt;
        &lt;/actions&gt;
      &lt;/errorHandler&gt;
    &lt;/handler&gt;
    &lt;handler eventType="PUSHTRANSLATIONS"&gt;
      &lt;scheduler executeSynchronously="false" /&gt;
      &lt;authorization type="authenticationContext" /&gt;
      &lt;execution timeout="01:00:00" recoveryGracePeriod="00:10:00" isolationLevel="None" useSingleThreadApartment="true" /&gt;
      &lt;activator&gt;
        &lt;comIEventHandler projectName="ISAuthorIso" className="CTranslationMgmt"&gt;
          &lt;configuration&gt;
            &lt;parameters&gt;
              &lt;parameter name="batchsize" type="value"&gt;1000&lt;/parameter&gt;
            &lt;/parameters&gt;
          &lt;/configuration&gt;
